        private void load_cbo_trang_thai()
        {
            m_trang_thai = false;
            BCTKUS.US_CM_DM_TU_DIEN v_us = new BCTKUS.US_CM_DM_TU_DIEN();
            BCTKDS.DS_CM_DM_TU_DIEN v_ds = new BCTKDS.DS_CM_DM_TU_DIEN();
            v_us.FillDataset(v_ds, "where id_loai_tu_dien = " + 12);
            m_cbo_trang_thai.DataSource = v_ds.CM_DM_TU_DIEN;
            m_cbo_trang_thai.ValueMember = CM_DM_TU_DIEN.ID;
            m_cbo_trang_thai.DisplayMember = CM_DM_TU_DIEN.TEN;
            DataRow v_dr = v_ds.CM_DM_TU_DIEN.NewRow();
            v_dr[CM_DM_TU_DIEN.ID] = -1;
            v_dr[CM_DM_TU_DIEN.ID_LOAI_TU_DIEN] = 12;
            v_dr[CM_DM_TU_DIEN.TEN] = "Tất cả";
            v_dr[CM_DM_TU_DIEN.TEN_NGAN] = "Tất cả";
            v_dr[CM_DM_TU_DIEN.MA_TU_DIEN] = "TRANG_THAI_THU";
            v_dr[CM_DM_TU_DIEN.GHI_CHU] = " ";
            v_ds.CM_DM_TU_DIEN.Rows.InsertAt(v_dr, 0);
            m_cbo_trang_thai.SelectedIndex = 0;
            m_trang_thai = true;
        }
        private void load_cbo_ten_pb()
        {
            m_trang_thai = false;
            US_DM_PHONG_BAN v_us = new US_DM_PHONG_BAN();
            DS_DM_PHONG_BAN v_ds = new DS_DM_PHONG_BAN();
            v_us.FillDataset(v_ds, "order by ten_phong_ban");
            m_cbo_ten_pb.DataSource = v_ds.DM_PHONG_BAN;
            m_cbo_ten_pb.ValueMember = DM_PHONG_BAN.ID;
            m_cbo_ten_pb.DisplayMember = DM_PHONG_BAN.TEN_PHONG_BAN;
            DataRow v_dr = v_ds.DM_PHONG_BAN.NewRow();
            v_dr[DM_PHONG_BAN.ID] = -1;
            v_dr[DM_PHONG_BAN.MA_PHONG_BAN] = "Tất cả";
            v_dr[DM_PHONG_BAN.TEN_PHONG_BAN] = "Tất cả";
            v_ds.DM_PHONG_BAN.Rows.InsertAt(v_dr, 0);
            m_cbo_ten_pb.SelectedIndex = 0;
            m_trang_thai = true;
        }
        private void tim_kiem()
        {
            decimal v_id_phong_ban = CIPConvert.ToDecimal(m_cbo_ten_pb.SelectedValue);
            decimal v_id_trang_thai = CIPConvert.ToDecimal(m_cbo_trang_thai.SelectedValue);
            string v_str_tu_khoa = m_txt_tu_khoa.Text;
            DateTime v_dt_tu_ngay;
            DateTime v_dt_den_ngay;
            DateTime today = DateTime.Today;
            if (!m_dt_tu_ngay.Checked)
            {
                DateTime starts = new DateTime(today.Year, today.Month, 1);
                m_dt_tu_ngay.Value = starts;
            }
            if (!m_dt_den_ngay.Checked)
            {
                DateTime last = new DateTime(today.Year, today.Month, DateTime.DaysInMonth(today.Year, today.Month));
                m_dt_den_ngay.Value = last;
            }
            else
            {
                v_dt_den_ngay = m_dt_den_ngay.Value;
            }
            v_dt_tu_ngay = m_dt_tu_ngay.Value;
            v_dt_den_ngay = m_dt_den_ngay.Value;
            US_V_TONG_HOP_CPN_THEO_PHONG_BAN v_us = new US_V_TONG_HOP_CPN_THEO_PHONG_BAN();
            DS_V_TONG_HOP_CPN_THEO_PHONG_BAN v_ds = new DS_V_TONG_HOP_CPN_THEO_PHONG_BAN();
            v_us.FillDatasetSearch(v_ds, v_str_tu_khoa, v_id_phong_ban, v_dt_tu_ngay, v_dt_den_ngay, v_id_trang_thai);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(v_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT(0, m_fg);
            //m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Count // chỗ này dùng hàm count tức là để đếm, có thể dùng các hàm khác thay thế
            //  , 0
            //  , (int)e_col_Number.TEN_PHONG_BAN // chỗ này là tên trường mà mình nhóm
            //  , (int)e_col_Number.SO_BILL // chỗ này là tên trường mà mình Count
            //  , "{0}"
            //  );
            //m_fg.Subtotal(C1.Win.C1FlexGrid.AggregateEnum.Sum
            //    , 0
            //    , (int)e_col_Number.TEN_PHONG_BAN
            //    , (int)e_col_Number.SO_TIEN
            //    , "{0}"
            //    );
            m_fg.Redraw = true;
        }
        private void export_2_excel()
        {
            CExcelReport v_obj_excel_report = new CExcelReport("f405_Tra_cuc_so_Bill.xlsx", 6, 1);
            v_obj_excel_report.AddFindAndReplaceItem("<tu_ngay>", m_dt_tu_ngay.Text);
            v_obj_excel_report.AddFindAndReplaceItem("<den_ngay>", m_dt_den_ngay.Text);
            v_obj_excel_report.AddFindAndReplaceItem("<phong_ban>", m_cbo_ten_pb.Text);
            v_obj_excel_report.AddFindAndReplaceItem("<trang_thai>", m_cbo_trang_thai.Text);
            v_obj_excel_report.FindAndReplace(false);
            v_obj_excel_report.Export2ExcelWithoutFixedRows(m_fg, 0, m_fg.Cols.Count - 1, true);
        }
