        private void load_data_2_cbo() {
            BCTKDS.DS_CM_DM_TU_DIEN v_ds_cm_dm_tu_dien = new BCTKDS.DS_CM_DM_TU_DIEN();
            BCTKUS.US_CM_DM_TU_DIEN v_us_cm_dm_tu_dien = new BCTKUS.US_CM_DM_TU_DIEN();
            v_us_cm_dm_tu_dien.FillDatasetByLoaiTuDienLoaiDinhMuc(v_ds_cm_dm_tu_dien);

            m_cbo_loai_dinh_muc.DataSource = v_ds_cm_dm_tu_dien.CM_DM_TU_DIEN;
            m_cbo_loai_dinh_muc.DisplayMember = CM_DM_TU_DIEN.TEN_NGAN;
            m_cbo_loai_dinh_muc.ValueMember = CM_DM_TU_DIEN.ID;
        }
    		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg){
			Hashtable v_htb = new Hashtable();
			v_htb.Add(RPT_CO_SO_DINH_MUC.TEN_NGAN, e_col_Number.TEN_NGAN);
			v_htb.Add(RPT_CO_SO_DINH_MUC.DON_GIA_DINH_MUC, e_col_Number.DON_GIA_DINH_MUC);
			v_htb.Add(RPT_CO_SO_DINH_MUC.AP_DUNG_TU_NGAY, e_col_Number.AP_DUNG_TU_NGAY);
			v_htb.Add(RPT_CO_SO_DINH_MUC.TEN, e_col_Number.TEN);
									
			ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg,v_htb,m_ds.RPT_CO_SO_DINH_MUC.NewRow());
			return v_obj_trans;			
		}
		private void load_data_2_grid(){						
			DS_RPT_CO_SO_DINH_MUC v_ds_rpt_co_so_dinh_muc = new DS_RPT_CO_SO_DINH_MUC();
            US_RPT_CO_SO_DINH_MUC v_us_rpt_co_so_dinh_muc = new US_RPT_CO_SO_DINH_MUC();
            string str_temp = m_dat_ap_dung_tu_ngay.Value.ToString();
            str_temp = str_temp.Substring(0, str_temp.IndexOf(" "));
            v_us_rpt_co_so_dinh_muc.FillDatasetByProc(v_ds_rpt_co_so_dinh_muc, CIPConvert.ToDecimal(m_cbo_loai_dinh_muc.SelectedValue), CIPConvert.ToDatetime(str_temp));
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(v_ds_rpt_co_so_dinh_muc, m_fg, m_obj_trans);
			m_fg.Redraw = true;
		}
		private void grid2us_object(US_RPT_CO_SO_DINH_MUC i_us
			, int i_grid_row) {
			DataRow v_dr;
			v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row,v_dr);
			i_us.DataRow2Me(v_dr);
		}

	
		private void us_object2grid(US_RPT_CO_SO_DINH_MUC i_us
			, int i_grid_row) {
			DataRow v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}


