		private void set_initial_form_load()
		{
			m_txt_tu_ngay.Text = CIPConvert.ToStr(DateTime.Now, "dd/MM/yyyy");
			m_txt_den_ngay.Text = CIPConvert.ToStr(DateTime.Now.AddMonths(2), "dd/MM/yyyy");
			m_obj_trans = get_trans_object(m_fg);
			load_data_2_grid();
		}
		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
		{
			Hashtable v_htb = new Hashtable();
			v_htb.Add(V_HT_VAN_THU_HISTORY.TEN, e_col_Number.TEN);
			v_htb.Add(V_HT_VAN_THU_HISTORY.GHI_CHU_1, e_col_Number.GHI_CHU_1);
			v_htb.Add(V_HT_VAN_THU_HISTORY.ID, e_col_Number.ID);
			v_htb.Add(V_HT_VAN_THU_HISTORY.GHI_CHU_2, e_col_Number.GHI_CHU_2);
			v_htb.Add(V_HT_VAN_THU_HISTORY.HANH_DONG, e_col_Number.HANH_DONG);
			v_htb.Add(V_HT_VAN_THU_HISTORY.ID_NGUOI_SU_DUNG, e_col_Number.ID_NGUOI_SU_DUNG);
			v_htb.Add(V_HT_VAN_THU_HISTORY.GHI_CHU, e_col_Number.GHI_CHU);
			v_htb.Add(V_HT_VAN_THU_HISTORY.THOI_DIEM, e_col_Number.THOI_DIEM);
			v_htb.Add(V_HT_VAN_THU_HISTORY.TEN_TRUY_CAP, e_col_Number.TEN_TRUY_CAP);

			ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.V_HT_VAN_THU_HISTORY.NewRow());
			return v_obj_trans;
		}
		private void load_data_2_grid()
		{
			m_ds = new DS_V_HT_VAN_THU_HISTORY();
			m_us.v_ht_van_thu_history_search_F660(m_ds
				, CIPConvert.ToDatetime(m_txt_tu_ngay.Text, "dd/MM/yyyy")
				, CIPConvert.ToDatetime(m_txt_den_ngay.Text, "dd/MM/yyyy")
				, m_txt_tu_khoa.Text.Trim());
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
			m_fg.Redraw = true;
		}
		private void grid2us_object(US_V_HT_VAN_THU_HISTORY i_us
			, int i_grid_row)
		{
			DataRow v_dr;
			v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
			i_us.DataRow2Me(v_dr);
		}


		private void us_object2grid(US_V_HT_VAN_THU_HISTORY i_us
			, int i_grid_row)
		{
			DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}


		private void insert_v_ht_van_thu_history()
		{
			//	F660_lich_su_van_thu_DE v_fDE = new  F660_lich_su_van_thu_DE();								
			//	v_fDE.display();
			load_data_2_grid();
		}

		private void update_v_ht_van_thu_history()
		{
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			grid2us_object(m_us, m_fg.Row);
			//	F660_lich_su_van_thu_DE v_fDE = new F660_lich_su_van_thu_DE();
			//	v_fDE.display(m_us);
			load_data_2_grid();
		}

		private void delete_v_ht_van_thu_history()
		{
			if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
			if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
			if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
			US_V_HT_VAN_THU_HISTORY v_us = new US_V_HT_VAN_THU_HISTORY();
			grid2us_object(v_us, m_fg.Row);
			try
			{
				v_us.BeginTransaction();
				v_us.Delete();
				v_us.CommitTransaction();
				m_fg.Rows.Remove(m_fg.Row);
			}
			catch (Exception v_e)
			{
				v_us.Rollback();
				CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
					new CDBClientDBExceptionInterpret());
