        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(V_GD_VAN_THU_ALL.NOI_NHAN, e_col_Number.NOI_NHAN);
            v_htb.Add(V_GD_VAN_THU_ALL.SO_CV_DEN, e_col_Number.SO_CV_DEN);
            v_htb.Add(V_GD_VAN_THU_ALL.MA_PHAP_NHAN, e_col_Number.MA_PHAP_NHAN);
            v_htb.Add(V_GD_VAN_THU_ALL.TRANG_THAI_ML, e_col_Number.TRANG_THAI_ML);
            v_htb.Add(V_GD_VAN_THU_ALL.SO_VA_KY_HIEU, e_col_Number.SO_VA_KY_HIEU);
            v_htb.Add(V_GD_VAN_THU_ALL.NGAY_LAP, e_col_Number.NGAY_LAP);
            v_htb.Add(V_GD_VAN_THU_ALL.NOI_GUI, e_col_Number.NOI_GUI);
            v_htb.Add(V_GD_VAN_THU_ALL.NGUOI_LAP, e_col_Number.NGUOI_LAP);
            v_htb.Add(V_GD_VAN_THU_ALL.GHI_CHU, e_col_Number.GHI_CHU);
            v_htb.Add(V_GD_VAN_THU_ALL.NGUOI_KY, e_col_Number.NGUOI_KY);
            v_htb.Add(V_GD_VAN_THU_ALL.NGAY_THANG_TREN_CONG_VAN, e_col_Number.NGAY_THANG_TREN_CONG_VAN);
            v_htb.Add(V_GD_VAN_THU_ALL.TEN_LOAI_VA_TRICH_YEU_ND, e_col_Number.TEN_LOAI_VA_TRICH_YEU_ND);
            v_htb.Add(V_GD_VAN_THU_ALL.NGUOI_NHAN__BAN_LUU_MPB, e_col_Number.NGUOI_NHAN__BAN_LUU_MPB);
            v_htb.Add(V_GD_VAN_THU_ALL.NOI_NGUOI_NHAN_MPB, e_col_Number.NOI_NGUOI_NHAN_MPB);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.V_GD_VAN_THU_ALL.NewRow());
            return v_obj_trans;
        }
        private void load_data_to_cbo_phap_nhan()
        {
            US_DM_PHAP_NHAN v_us = new US_DM_PHAP_NHAN();
            DS_DM_PHAP_NHAN v_ds = new DS_DM_PHAP_NHAN();
            v_us.FillDataset(v_ds, "order by " + DM_PHAP_NHAN.MA_PHAP_NHAN);

            DataRow v_dr = v_ds.Tables[0].NewRow();
            v_dr[DM_PHAP_NHAN.ID] = -1;
            v_dr[DM_PHAP_NHAN.GHI_CHU] = "";
            v_dr[DM_PHAP_NHAN.MA_PHAP_NHAN] = "Tất cả";
            v_dr[DM_PHAP_NHAN.TEN_PHAP_NHAN] = "Tất cả";
            v_ds.Tables[0].Rows.InsertAt(v_dr, 0);
            v_ds.AcceptChanges();

            m_cbo_phap_nhan.DisplayMember = DM_PHAP_NHAN.MA_PHAP_NHAN;
            m_cbo_phap_nhan.ValueMember = DM_PHAP_NHAN.ID;
            m_cbo_phap_nhan.DataSource = v_ds.DM_PHAP_NHAN;

        }
        private void load_data_2_grid()
        {
            m_ds = new DS_V_GD_VAN_THU_ALL();
            decimal v_dc_id_loai_cong_van = -1;
            //if (m_rdb_van_ban_di_khong_luu.Checked == true) v_dc_id_loai_cong_van = ID_LOAI_VAN_THU.CONG_VAN_DI_KHONG_LUU;
            //else 
            if (m_rdb_van_ban_di_luu.Checked == true) v_dc_id_loai_cong_van = ID_LOAI_VAN_THU.CONG_VAN_DI;
            else if (m_rdb_van_ban_den.Checked == true) v_dc_id_loai_cong_van = ID_LOAI_VAN_THU.CONG_VAN_DEN;
            m_us.Search(m_ds, CIPConvert.ToDecimal(m_cbo_phap_nhan.SelectedValue), v_dc_id_loai_cong_van, CIPConvert.ToDatetime(m_txt_tu_ngay.Text, "dd/MM/yyyy"), CIPConvert.ToDatetime(m_txt_den_ngay.Text, "dd/MM/yyyy"), m_txt_tu_khoa.Text.Trim());
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            //for (int i = m_fg.Row; i < m_fg.Rows.Count; i++)
            //{
            //    for (int j = m_fg.Col; j < m_fg.Cols.Count; j++)
            //    {
            //        if (m_fg.Cols[j].Visible==true)
            //        CGridUtils.WrapWordInOneCell(m_fg, i,j);
            //    }
            //}
            m_fg.Redraw = true;
            format_grid();
        }
        private void grid2us_object(US_V_GD_VAN_THU_ALL i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }
        private void us_object2grid(US_V_GD_VAN_THU_ALL i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }

