		private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg){
			Hashtable v_htb = new Hashtable();
			v_htb.Add(CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.THANH_TIEN, e_col_Number.THANH_TIEN);
			v_htb.Add(CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.TEN_PHONG_BAN, e_col_Number.TEN_PHONG_BAN);
			v_htb.Add(CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.MA_PHONG_BAN, e_col_Number.MA_PHONG_BAN);
			v_htb.Add(CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.VAT, e_col_Number.VAT);
			v_htb.Add(CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.PHONG_BAN_ID, e_col_Number.PHONG_BAN_ID);
			v_htb.Add(CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.TONG_SO_TIEN, e_col_Number.TONG_SO_TIEN);
									
			ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg,v_htb,m_ds.CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH.NewRow());
			return v_obj_trans;			
		}
		private void load_data_2_grid(){						
			m_ds = new DS_CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH();			
			m_us.FillDataset(m_ds);
			m_fg.Redraw = false;
			CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
			m_fg.Redraw = true;
		}
		private void grid2us_object(US_CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH i_us
			, int i_grid_row) {
			DataRow v_dr;
			v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			m_obj_trans.GridRow2DataRow(i_grid_row,v_dr);
			i_us.DataRow2Me(v_dr);
		}

	
		private void us_object2grid(US_CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH i_us
			, int i_grid_row) {
			DataRow v_dr = (DataRow) m_fg.Rows[i_grid_row].UserData;
			i_us.Me2DataRow(v_dr);
			m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
		}
        public bool IsNumber(string pText)
        {
            Regex regex = new Regex(@"^[-+]?[0-9]*\.?[0-9]+$");
            return regex.IsMatch(pText);
        }
