        private void show_du_lieu_thong_tin_thong_ke()
        {
            m_ds = new DS_CM_DM_TONG_HOP_TIEN_CHUYEN_PHAT_NHANH();
            m_us.load_du_lieu_thong_ke_chuyen_phat_nhanh(m_ds, m_dtp_thong_ke_thang.Value);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT((int)e_col_Number.STT, m_fg);
            m_fg.Redraw = true;
        }
