        private void m_cmd_xuat_excel_Click(object sender, EventArgs e)
        {
            try
            {
                CExcelReport v_exel_obj = new CExcelReport("BCTK_Chuyen_phat_nhanh.xlsx", 5, 1);
                v_exel_obj.AddFindAndReplaceItem("<THANG>", m_dtp_thong_ke_thang.Text);
                //v_exel_obj.AddFindAndReplaceItem("<TONG_SO_TIEN>", Tinh_tong_so_tien(m_fg).ToString());
                //v_exel_obj.AddFindAndReplaceItem("<TONG_VAT>", Tinh_tong_VAT(m_fg).ToString());
                //v_exel_obj.AddFindAndReplaceItem("<THANH_TIEN>", Tinh_tong_thanh_tien(m_fg).ToString());
                v_exel_obj.AddFindAndReplaceItem("<NGAY_LAP>", DateTime.Now.ToString("dd/MM/yyyy"));
                v_exel_obj.FindAndReplace(false);
                v_exel_obj.Export2ExcelWithoutFixedRows(m_fg,(int)e_col_Number.STT,(int)e_col_Number.THANH_TIEN, false);
            }
            catch (Exception v_e)
            {
                CSystemLog_301.ExceptionHandle(v_e);
            }
        }
