        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(V_GD_VAN_THU_ALL.NOI_NHAN, e_col_Number.NOI_NHAN);
            v_htb.Add(V_GD_VAN_THU_ALL.SO_CV_DEN, e_col_Number.SO_CV_DEN);
            v_htb.Add(V_GD_VAN_THU_ALL.MA_PHAP_NHAN, e_col_Number.MA_PHAP_NHAN);
            v_htb.Add(V_GD_VAN_THU_ALL.TRANG_THAI_ML, e_col_Number.TRANG_THAI_ML);
            v_htb.Add(V_GD_VAN_THU_ALL.SO_VA_KY_HIEU, e_col_Number.SO_VA_KY_HIEU);
            v_htb.Add(V_GD_VAN_THU_ALL.NGAY_LAP, e_col_Number.NGAY_LAP);
            v_htb.Add(V_GD_VAN_THU_ALL.NOI_GUI, e_col_Number.NOI_GUI);
            v_htb.Add(V_GD_VAN_THU_ALL.NGUOI_LAP, e_col_Number.NGUOI_LAP);
            v_htb.Add(V_GD_VAN_THU_ALL.GHI_CHU, e_col_Number.GHI_CHU);
            v_htb.Add(V_GD_VAN_THU_ALL.NGUOI_KY, e_col_Number.NGUOI_KY);
            v_htb.Add(V_GD_VAN_THU_ALL.NGAY_THANG_TREN_CONG_VAN, e_col_Number.NGAY_THANG_TREN_CONG_VAN);
            v_htb.Add(V_GD_VAN_THU_ALL.TEN_LOAI_VA_TRICH_YEU_ND, e_col_Number.TEN_LOAI_VA_TRICH_YEU_ND);
            v_htb.Add(V_GD_VAN_THU_ALL.NGUOI_NHAN__BAN_LUU_MPB, e_col_Number.NGUOI_NHAN__BAN_LUU_MPB);
            v_htb.Add(V_GD_VAN_THU_ALL.NOI_NGUOI_NHAN_MPB, e_col_Number.NOI_NGUOI_NHAN_MPB);
