        private void update_v_gd_van_thu_all()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            grid2us_object(m_us, m_fg.Row);
            if (m_us.dcID_LOAI_CONG_VAN == ID_LOAI_VAN_THU.CONG_VAN_DI_NOI_BO)
            {
                F651_CAP_NHAT_CONG_VAN_DI v_frm = new F651_CAP_NHAT_CONG_VAN_DI();
                v_frm.display_for_update(m_us.dcID, F651_CAP_NHAT_CONG_VAN_DI.eFormMode.DI_NOI_BO);
            }
            else if (m_us.dcID_LOAI_CONG_VAN == ID_LOAI_VAN_THU.CONG_VAN_DI_BEN_NGOAI)
            {
                F651_CAP_NHAT_CONG_VAN_DI v_frm = new F651_CAP_NHAT_CONG_VAN_DI();
                v_frm.display_for_update(m_us.dcID, F651_CAP_NHAT_CONG_VAN_DI.eFormMode.DI_BEN_NGOAI);
            }
            //else if (m_us.dcID_LOAI_CONG_VAN == ID_LOAI_VAN_THU.CONG_VAN_DI_KHONG_LUU)
            //{
            //    F651_CAP_NHAT_CONG_VAN_DI v_frm = new F651_CAP_NHAT_CONG_VAN_DI();
            //    v_frm.display_for_update(m_us.dcID, F651_CAP_NHAT_CONG_VAN_DI.eFormMode.DI_KHONG_LUU);
            //}
            else if (m_us.dcID_LOAI_CONG_VAN == ID_LOAI_VAN_THU.CONG_VAN_DEN)
            {
                F652_CAP_NHAT_CONG_VAN_DEN v_frm = new F652_CAP_NHAT_CONG_VAN_DEN();
                v_frm.display_for_update(m_us.dcID);
            }
            load_data_2_grid();
        }

        private void delete_v_gd_van_thu_all()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg, m_fg.Row)) return;
            if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
            US_V_GD_VAN_THU_ALL v_us = new US_V_GD_VAN_THU_ALL();
            US_GD_VAN_THU v_us_gd_van_thu = new US_GD_VAN_THU();
            grid2us_object(v_us, m_fg.Row);
            try
            {
                v_us_gd_van_thu = new US_GD_VAN_THU(v_us.dcID);
                v_us_gd_van_thu.BeginTransaction();
                v_us_gd_van_thu.Delete();
                v_us_gd_van_thu.CommitTransaction();
                m_fg.Rows.Remove(m_fg.Row);
            }
            catch (Exception v_e)
            {
                v_us_gd_van_thu.Rollback();
                CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
                    new CDBClientDBExceptionInterpret());
                v_objErrHandler.showErrorMessage();
            }
        }

